function getSamePageAnchor(o){return o.protocol===window.location.protocol&&o.host===window.location.host&&o.pathname===window.location.pathname&&o.search===window.location.search&&o.hash}function scrollToHash(o,e){const n=!!o&&document.querySelector(o);n&&(e&&e.preventDefault(),gsap.to(window,{scrollTo:n}))}document.querySelectorAll("a[href]").forEach((o=>{o.addEventListener("click",(e=>{scrollToHash(getSamePageAnchor(o),e)}))})),scrollToHash(window.location.hash);